import React, { useState } from 'react';
import { days } from './days.js'; // Asegúrate de que la ruta sea correcta según la ubicación de tu archivo days.js

function ControlledSelect() {
  const [value, setValue] = useState('Lunes'); // Establece el valor inicial como 'Lunes' o cualquier valor predeterminado que desees.

  const handleChange = (event) => {
    setValue(event.target.value);
  };

  return (
    <form>
        <select value={value} onChange={handleChange}>
          {days.items.map((day) => (
            <option key={day.name} value={day.name}>
              {day.name}
            </option>
          ))}
        </select>
    </form>
  );
}

export default ControlledSelect;
