import React, { useContext, useState, useEffect } from "react";
import AppContext from "../AppContext";
import "./DropdownMenu.css";
import { notifications } from "./notifications";  // Importa el objeto notifications desde notificaciones.js

function DropdownMenu() {
  function DropdownItem(props) {
    return (
      <div className="dropdown_menu-item">
        <div className="dropdown_menu-item-content">
          <div className={`dropdown_notification-text ${props.centered ? 'centered' : ''}`}>{props.children}</div>
          <div className="dropdown_notification-time">{props.timeAgo}</div>
        </div>
      </div>
    );
  }

  const { loadNotifications } = useContext(AppContext);

  const [groupNotifications, setGroupNotifications] = useState([]);

  useEffect(() => {
    loadNotifications().then((groups) => {
      const notifications = [];
      for (let i = 0; i < groups.length; i += 2) {
        notifications.push(groups.slice(i, i + 2));
      }
      setGroupNotifications(notifications);
    });
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const notificationTimes = groupNotifications.flatMap(group => group.map(item => parseInt(item.time)));
  const timeUnits = groupNotifications.flatMap(group => group.map(item => item.unit));

  const [visibleNotifications, setVisibleNotifications] = useState(5);
  const [menuOpen] = useState(false);

  const loadMoreNotifications = () => {
    const nextVisibleNotifications = visibleNotifications + 3;
    if (nextVisibleNotifications <= groupNotifications.flat().length) {
      setVisibleNotifications(nextVisibleNotifications);
    }
  };

  return (
    <div className={`dropdown ${menuOpen ? 'open' : ''}`}>
      <div className="dropdown_content">
        {groupNotifications.flat().slice(0, visibleNotifications).map((notification, index) => (
          <DropdownItem key={index} centered timeAgo={`${notificationTimes[index]} ${timeUnits[index]} ago`}>
            {notification.name}
          </DropdownItem>
        ))}
      </div>
      <button
        className="dropdown_item-button dropdown_load-more-button"
        onClick={loadMoreNotifications}
        type="button"
      >
        Cargar más
      </button>
    </div>
  );
}

export default DropdownMenu;
