import React, { useState } from 'react';
import "./Customization.css";
import ControlledSelect from './ControlledSelect';
import CheckBox from './Checkbox';
import Slider from './Slider';
import ColorPicker from "./ColorPicker";

function Customization() {
    const [customization, setCustomization] = useState(false);

    const toggleCustom = () => {
        setCustomization(!customization);
    }

    if (customization) {
        document.body.classList.add('active-customization');
    } else {
        document.body.classList.remove('active-customization');
    }

    return (
        <div className="customization-container">
            <button
                onClick={toggleCustom}
                className="btn-customization">
                Personalizar
            </button>

            {customization && (
                <div className="customization">
                    <div
                        onClick={toggleCustom}
                        className="overlay"></div>
                    <div className="customization-content">
                        <h2>Personalización de Horario</h2>
                        <div className="select-container">
                            <div className="container">
                                <div className="select-label">
                                    Primer día:
                                </div>
                                <ControlledSelect />
                            </div>
                            <div className="container">
                                <div className="select-label">
                                    Último día:
                                </div>
                                <ControlledSelect />
                            </div>
                        </div>

                        <div className="select-container">
                            <div className="container">
                                <div className="select-label">
                                    Hora inicio:
                                </div>
                                <CheckBox />
                            </div>
                            <div className="container">
                                <div className="select-label">
                                    Hora fin:
                                </div>
                                <CheckBox />
                            </div>
                        </div>

                        <div className="container">
                            <div className="select-label">
                                Selecciona el tamaño del texto:
                            </div>
                            <Slider />
                        </div>

                        <div className="container2">
                            <div className="select-label2">
                                Selecciona el color del texto:
                            </div>
                            <ColorPicker />
                        </div>

                        <button
                            className="close-customization"
                            onClick={toggleCustom}>
                            Guardar
                        </button>
                    </div>
                </div>
            )}
        </div>
    );
}

export default Customization;
